<template>
	<view class="my-refresh">
		<!-- type loading  -->
		<view  v-if="loading && pullDownType == 'loading'" class="my-loading">
			<view class="my-loading-icon">
				<image src="data:image/png;base64," mode="widthFix"></image>
			</view>
			<view class="my-loading-text">{{pullDownText}}</view>
		</view>
		<!-- type dot -->
		<view class="my-loading" v-if="loading && pullDownType == 'dot'" style="align-items:center">
			<view class="dot" :style="{animationPlayState: playState,'border-color':borderColor}"></view>
			<view class="dot" :style="{animationPlayState: playState,'border-color':borderColor}"></view>
			<view class="dot" :style="{animationPlayState: playState,'border-color':borderColor}"></view>
		</view>
		<view class="my-refresh-action" @touchstart="start" @touchmove="move" @touchend="end" :style="{ transform: `translate3d(0,${position}px,0)` }">
			<scroll-view :style="getHeight"  scroll-y="true"  @scrolltoupper="scrolltoupper" @scrolltolower="scrolltolower">
				<slot></slot>
				<view class="my-refresh-more-loading">
					<view class="my-loading-icon" v-if="loadMore">
						<image src="data:image/png;base64," mode="widthFix"></image>
					</view>
					<view class="load-more-text">
						{{getLoadingText}}
					</view>
				</view>
			</scroll-view>
		</view>
		
	</view>
</template>

<script>
	// import mixin from './mixins.js'
	export default {
		// mixins: [mixin],
		data(){
			return {
				loading:false,
				startY:null,
				position:null,
				direction:null,//1为下拉 2为上拉
				loadMore:false,
				action:false,
				playState:'paused'
			}
		},
		props:{
			borderColor:{
				type:String,
				default:'#66A6FF'
			},
			pullDownType:{
				type:String,
				default:'loading'
			},
			pullDown:{
				type:Boolean,
				default:true
			},
			pullUp:{
				type:Boolean,
				default:true
			},
			// 当前页数
			currentPage:{
				type:Number,
				default:0
			},
			// 总页数
			totalPage:{
				type:Number,
				default:0
			},
			pullDownText:{
				type:String,
				default:'下拉加载'
			},
			contentText: {
				type: Object,
				default () {
					return {
						contentdown: '上拉显示更多',
						contentrefresh: '正在加载...',
						contentnomore: '没有更多数据了'
					}
				}
			}
		},
		computed:{
			getHeight() {
				let height = uni.getSystemInfoSync().windowHeight
				console.log(height)
				return `height: ${height}px;`
			},
			getLoadingText(){
				const {loadMore,currentPage,totalPage,contentText} = this
				if(loadMore){
					return contentText.contentrefresh
				}else if(totalPage > currentPage){
					return contentText.contentdown
				}else{
					return contentText.contentnomore
				}
			}
		},
		methods:{
			scrolltoupper(e){
				console.log('顶部')
				
			},
			scrolltolower(e){
				if(this.pullUp){
					if(this.action) return;
					if(this.totalPage > this.currentPage){
						setTimeout(()=>{
							this.loadMore = true
							this.action = true
							this.$emit('pullUp',this.upNext)
						},200)
					}
					
				}
			},
			start(e){
				if(this.action) return;
				this.startY = e.touches[0].clientY
			},
			move(e){
				if(this.action) return;
				const move = e.touches[0].clientY - this.startY
				if(move > 0){
					// 下拉
					this.playState = 'running'
					if(this.pullDown && move > 40){
						this.position = 40
						this.loading = true
						this.action  = true
						this.$emit('pullDown',this.downNext)
						this.direction = 1
					}else if(this.pullDown){
						this.position  = move
					}
				}else{
					return;
				}
			},
			end(e){
				if(this.direction == '1' && this.pullDown){
						this.downNext()
					this.direction = null
				}else{
					//结束时上拉
					return;
				}
			},
			downNext(){
				this.loading = false
				this.position = 0
				this.action = false
				this.playState = 'paused'
			},
			upNext(){
				this.loadMore = false
				this.action = false
			}
		}
	}
</script>

<style scoped>
	page{
		overflow: hidden;
	}
	.my-refresh{
		width: 100%;
		height:100%;
		position: relative;
		overflow: hidden;
		/* margin-bottom: 50px; */
	}
	/* .refresh-message {
		width: 100%;
		height: 50px;
		background-color: red;
		display: flex;
		justify-content: center;
	} */
	.my-loading,.my-refresh-more-loading{
		width: 100%;
		height: 40px;
		background-color: white;
		display: flex;
		justify-content: center;
		line-height: 40px;
	}
	.my-refresh-action{
		width: 100%;
		transform: translate(0);
		transition-duration: 0.3s;
	}
	.my-loading-icon{
		width: 12px;
		height: 12px;
		margin-right:10px;
	}
	.my-loading-text,.my-load-more-text{
		font-size: 12px;
	}
	.my-loading-icon image{
		width: 12px;
		height: 12px;
		animation: loading 1s 0s step-end infinite;
	}
	@keyframes loading {
		0% {
			transform: rotate(0deg);
		}
	
		8% {
			transform: rotate(30deg);
		}
	
		16% {
			transform: rotate(60deg);
		}
	
		24% {
			transform: rotate(90deg);
		}
	
		32% {
			transform: rotate(120deg);
		}
	
		40% {
			transform: rotate(150deg);
		}
	
		48% {
			transform: rotate(180deg);
		}
	
		56% {
			transform: rotate(210deg);
		}
	
		64% {
			transform: rotate(240deg);
		}
	
		73% {
			transform: rotate(270deg);
		}
	
		82% {
			transform: rotate(300deg);
		}
	
		91% {
			transform: rotate(330deg);
		}
	
		100% {
			transform: rotate(360deg);
		}
	}
	.my-loading .dot {
	  width: 15px;
	  height: 15px;
	  margin: 0 calc(15px / 2);
	  border: calc(15px / 5) solid #66A6FF;
	  border-radius: 50%;
	  float: left;
	  transform: scale(0);
	  animation: loading-dot 1000ms ease infinite 0ms;
	}
	 
	.my-loading .dot:nth-child(1) {
	  animation-delay: calc(300ms * 1);
	}
	 
	.my-loading .dot:nth-child(2) {
	  animation-delay: calc(300ms * 2);
	}
	 
	.my-loading .dot:nth-child(3) {
	  animation-delay: calc(300ms * 3);
	 
	}
	 
	@keyframes loading-dot {
	  50% {
		transform: scale(1);
		opacity: 1;
	  }
	  100% {
		opacity: 0;
	  }
	}
</style> 
